import React, { useEffect, useState } from 'react';
import './index.css';

function Intro() {
  const [introText, setIntroText] = useState('');
  const [subText, setSubText] = useState('');

  useEffect(() => {
    const intro = "Bendareur Ayoub";
    const sub = "Etudiant informatique à Epitech Lyon";
    let introIndex = 0;
    let subIndex = 0;

    const typeIntro = () => {
      if (introIndex < intro.length) {
        setIntroText(prev => prev + intro[introIndex]);
        introIndex++;
        setTimeout(typeIntro, 100);
      } else {
        typeSub();
      }
    };

    const typeSub = () => {
      if (subIndex < sub.length) {
        setSubText(prev => prev + sub[subIndex]);
        subIndex++;
        setTimeout(typeSub, 100);
      }
    };

    typeIntro();
  }, []);

  return (
    <section id="intro">
      {/* <BgVideo/> */}
      <h1 className="typing">{introText}</h1>
      <h3 className="typing">{subText}</h3>
      {/* citation */}
    </section>
  );
}

export default Intro;
