import portrait from '../src/img/Shin.jpg';
import user from '../src/img/person.png';
import projets from '../src/img/projets.png';
import skills from '../src/img/skills.png';
import contact from '../src/img/contact.png'
import './App.css';
import './style.css'

import React, { useState, useEffect, useRef } from 'react';
/*import {Link} from 'react-scroll';*/


function App() {

  /* const scrollToSectionP = useRef<HTMLDivElement | null>(null); */

  const [open, setOpen] = useState(false);
  let menuRef = useRef();

  useEffect(() => {
    let handler = (event)=>{
      if(!menuRef.current.contains(event.target)){
        setOpen(true);
        console.log(menuRef.current);
      }
    };

    document.addEventListener("mousedown", handler);


    return() =>{
      document.removeEventListener("mousedown", handler);
    }

  });

  function DropdownItem(props) {
    return (
      <li className='dropdownItem' onClick={() => scrollToSection(props.sectionId)}>
        <img src={props.img} alt="Dropdown Item" />
        <a href={`#${props.sectionId}`}> {props.text} </a>
      </li>
    );
  }

  return (
    <div className="App">
      <div className="menu-container" ref={menuRef}>
        <div className="menu-trigger" onClick={() => setOpen(!open)}>
          <img src={portrait} alt="Portrait" />
        </div>

        <div className={`dropdown-menu ${open ? 'active' : 'inactive'}`}>
          <h3>Bendareur Ayoub<br /><span>Etudiant Epitech Lyon</span></h3>
          <ul>
            <DropdownItem img={user} text="Présentation" sectionId="presentation" />
            <DropdownItem img={skills} text="Compétence" sectionId="competence" />
            <DropdownItem img={projets} text="Projets réalisés" sectionId="projets-realises" />
            <DropdownItem img={contact} text="Contact" sectionId="contact" />
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
